import React, { Component } from 'react';
import API from "../../utils/API";

class InputBudget extends Component {
    state = {
        item: "",
        income: "",
        bills: "",
        cost: "",
        save: ""
    };

    componentDidMount() {
        this.loadSave();
    };

    loadSave = () => {
        API.getSaves()
            .then(res =>
                this.setState({ item: res.data, income: "", bills: "", cost: "", save: "" })
            )
            .catch(err => console.log(err));
    };

    handleInputChange = e => {
        const { name, value } = e.target;
        this.setState({
            [name]: value
        });
        console.log(this.state.income);
        console.log(this.state.bills);
    };

    handleFormSubmit = e => {
        e.preventDefault();

        this.setState({
            item: "",
            income: "",
            bills: ""
        });

        let item = (`${this.state.item}`);
        let inc = parseInt(`${this.state.income}`);
        let bil = parseInt(`${this.state.bills}`);
        let leftoverIncome = (inc - bil);

        console.log(item)
        console.log(leftoverIncome)
    };

    render(props) {
        return (
            <div className="card card-body mb-3">
                <form>
                    <label>Item</label>
                    <input
                        className="form-control"
                        name="item"
                        placeholder=" Item you're saving for!"
                        value={this.state.item}
                        onChange={(e) => this.handleInputChange(e)}
                    />
                    <label>Monthly Income</label>
                    <input
                        className="form-control"
                        name="income"
                        placeholder=" Input Estimated Monthly Income - 0.00"
                        value={this.state.income}
                        onChange={(e) => this.handleInputChange(e)}
                    />
                    <label>Monthly Expense</label>
                    <input
                        className="form-control"
                        name="bills"
                        placeholder="Input Estimated Monthly Expense - 0.00"
                        value={this.state.bills}
                        onChange={(e) => this.handleInputChange(e)}
                    />
                    <button
                        className="btn btn-dark btn-block mt-3"
                        onClick={(e) => this.handleFormSubmit(e)}
                    >Submit</button>
                </form>
            </div>
        )
    }
}

export default InputBudget;
