import React, { Component } from 'react';

class InputExpenses extends Component {

    state = {
        item: "",
        income: "",
        bills: "",
        cost: "",
        save: ""
    };

    handleInputChange = e => {
        const { name, value } = e.target;
        this.setState({
            [name]: value
        });
        console.log(this.state.cost);
        console.log(this.state.save);
    };
    handleFormSave = e => {
        e.preventDefault();

        this.setState({
            cost: "",
            save: ""
        });

        let cost = parseInt(`${this.state.cost}`);
        let save = parseInt(`${this.state.save}`);

        console.log(cost);
        console.log(save);
    };

    render() {
        return (
            <div className="card card-body">
                <form>
                <label>Cost</label>
                    <input
                        className="form-control"
                        name="cost"
                        placeholder=" Cost of item you're saving for!"
                        value={this.state.cost}
                        onChange={(e) => this.handleInputChange(e)}
                    />
                    <label>Amount To Save</label>
                    <input
                        className="form-control"
                        name="save"
                        placeholder=" How much would you like to save?"
                        value={this.state.save}
                        onChange={(e) => this.handleInputChange(e)}
                    />
                    <button
                        className="btn btn-dark btn-block mt-3"
                        onClick={(e) => this.handleFormSave(e)}
                        >Save</button>
                </form>
            </div>
        )
    }
}

export default InputExpenses;
